let currentScene = "nachalo";

function renderVariants(branchName) {
    if (!game[branchName].variants) {
        return "";
    }
    let content = "<ul>";
    for (let variant in game[branchName].variants) {
        content += `<li><a onclick="selectScene('${variant}')">${game[branchName].variants[variant]}</a></li>`;
    }
    content += "</ul>";
    return content;
}

function renderGame() {
    let gameContainer = document.getElementById("gameContainer");
    let content = game[currentScene].content;
    content += renderVariants(currentScene);
    gameContainer.innerHTML = content;
}

function selectScene(scene) {
    currentScene = scene;
    renderGame();
    window.scrollTo(0, 0);
}

window.onload = renderGame;
